import React from "react";
import "./header.css";
import logo from '../../images/logo.png';
import Modal from "@material-ui/core/Modal";
import ConnectModal from "../model/connectModal";
const Header = () => {

  const [openConnect, setOpenConnect] = React.useState(false);

  const handleConnectOpen = () => {
    setOpenConnect(true);
  };

  const handleConnectClose = () => {
    setOpenConnect(false);
  };
  return (
    <div>
       {openConnect &&  <Modal
              open={handleConnectOpen}
              onClose={handleConnectClose}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              <ConnectModal />
            </Modal> }
      <nav class="navbar navbar-expand-lg mainNavbar ">
        <div class="container">
          <a class="navbar-brand" href="#">
             <img src={logo} width="60" height="60" alt="" />
          </a>
          <button
            class="navbar-toggler customToggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav ms-auto">
              <li class="nav-item">
                <a class="nav-link ms-3 ms-lg-0" href="#">
                  CONTACT US
                </a>
              </li>
              <li class="nav-item ms-3">
                
               {/* <a href="" className="nav-link connectWallet">    CONNECT WALLET</a> */}
                <button className="btn nav-link connectWallet" onClick={handleConnectOpen}> CONNECT WALLET</button>
                 
              </li>
            
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Header;
